--
-- Table structure for table `chainat_place`
--

CREATE TABLE IF NOT EXISTS `chainat_place` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(200) CHARACTER SET tis620 NOT NULL,
  `place_type` enum('ท่องเที่ยว','วัด','ร้านอาหาร','otop') NOT NULL,
  `district` enum('เมืองชัยนาท','สรรพยา','สรรคบุรี','วัดสิงห์','หันคา','มโนรมย์','เนินขาม','หนองมะโมง') NOT NULL,
  `details` text NOT NULL,
  `phone` varchar(200) NOT NULL,
  `opening_time` varchar(200) NOT NULL,
  `address` varchar(500) NOT NULL,
  `latitude` decimal(10,7) NOT NULL,
  `longitude` decimal(10,7) NOT NULL,
  `image_list` varchar(50) NOT NULL,
  `image_cover` varchar(50) NOT NULL,
  `recommend` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=13 ;

